import React from 'react';

let sizes = '';
const Sizes = () => {
    return (
        <div>
            <p className="size" id="frameSize">
                Frame size: {sizes}
            </p>
            <div className="other-size">
                <p>Other size:</p>
                <div className="btnSize">
                    <button id="x3" className="xBtn" value="3x3" onClick={sizes = '3x3'}>3x3</button>
                    <button id="x4" className="xBtn" value="4x4" onClick={sizes = '4x4'}>4x4</button>
                    <button id="x5" className="xBtn" value="5x5"  onClick={sizes = '5x5'}>5x5</button>
                    <button id="x6" className="xBtn" value="6x6" onClick={sizes = '6x6'}>6x6</button>
                    <button id="x7" className="xBtn" value="7x7">7x7</button>
                    <button id="x8" className="xBtn" value="8x8">8x8</button>
                </div>
            </div>
        </div>
    );
};

export default Sizes;
